import { Component, inject, Input } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserService } from 'src/app/services/user.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent {




  registerForm: FormGroup;
  submitted = false;

  constructor(private formBuilder: FormBuilder, private userService: UserService, private router: Router) {
    this.registerForm = this.formBuilder.group({
      first_name: ['', [Validators.required, Validators.minLength(2)]],
      last_name: ['', [Validators.required, Validators.minLength(2)]],
      email: ['', [Validators.required, Validators.email]],
      mobile_no: ['', [Validators.required, Validators.pattern('^[0-9]{10}$')]],  // Validating for 10 digits
      password: ['', [Validators.required, Validators.minLength(6)]],
      role: ['user', Validators.required]
    });
  }

  get f() {
    return this.registerForm.controls;
  }

  register() {
    this.submitted = true;

    if (this.registerForm.invalid) {
      return;
    }

    const user: any = this.registerForm.value;
    this.userService.register(user).then((result: any) => {
      if (result.status = 'SUCCESS') {
        this.router.navigate(['/login']);
      }else{
        alert(result.message);
      }
    });
  }
}

<form [formGroup]="registerForm" (ngSubmit)="register()">
  <div>
    <label>First Name:</label>
    <input formControlName="first_name" />
    <div *ngIf="submitted && f['first_name'].errors">
      <small *ngIf="f['first_name'].errors['required']">First Name is required.</small>
      <small *ngIf="f['first_name'].errors['minlength']">First Name must be at least 2 characters long.</small>
    </div>
  </div>

  <div>
    <label>Last Name:</label>
    <input formControlName="last_name" />
    <div *ngIf="submitted && f['last_name'].errors">
      <small *ngIf="f['last_name'].errors['required']">Last Name is required.</small>
      <small *ngIf="f['last_name'].errors['minlength']">Last Name must be at least 2 characters long.</small>
    </div>
  </div>

  <div>
    <label>Email:</label>
    <input formControlName="email" type="email" />
    <div *ngIf="submitted && f['email'].errors">
      <small *ngIf="f['email'].errors['required']">Email is required.</small>
      <small *ngIf="f['email'].errors['email']">Please enter a valid email.</small>
    </div>
  </div>

  <div>
    <label>Mobile No:</label>
    <input formControlName="mobile_no" />
    <div *ngIf="submitted && f['mobile_no'].errors">
      <small *ngIf="f['mobile_no'].errors['required']">Mobile No is required.</small>
      <small *ngIf="f['mobile_no'].errors['pattern']">Please enter a valid 10-digit mobile number.</small>
    </div>
  </div>

  <div>
    <label>Password:</label>
    <input formControlName="password" type="password" />
    <div *ngIf="submitted && f['password'].errors">
      <small *ngIf="f['password'].errors['required']">Password is required.</small>
      <small *ngIf="f['password'].errors['minlength']">Password must be at least 6 characters long.</small>
    </div>
  </div>

  <div>
    <label>Role:</label>
    <select formControlName="role">
      <option value="User">User</option>
      <option value="Admin">Admin</option>
      <option value="Guest">Guest</option>
    </select>
  </div>

  <button type="submit">Register</button>
</form>
