import { Component, inject, Input } from '@angular/core';
import { NgbActiveOffcanvas, NgbModal, NgbModalRef } from '@ng-bootstrap/ng-bootstrap';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserService } from 'src/app/services/user.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {

  loginForm: FormGroup;
  submitted = false;

  constructor(
    private formBuilder: FormBuilder,
    private userService: UserService,
    private router: Router
  ) {
    this.loginForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  get f() {
    return this.loginForm.controls;
  }

  login() {
    this.submitted = true;

    if (this.loginForm.invalid) {
      return;
    }

    let data = {
      "email":this.loginForm.value.email,
      "password":this.loginForm.value.password,
    }
    this.userService.login(data).then((result: any) => {
      if (result.status = 'SUCCESS') {
        localStorage.setItem('name', result.name);
        localStorage.setItem('id', result.id);
        localStorage.setItem('token', result.token);
        localStorage.setItem('role', result.role);
        this.router.navigate(['/users']);
      }else{
        alert(result.message);
      }
    });

  }
}
<div *ngIf="isLoading" class="loading-overlay">
  <div class="loading-spinner"></div>
</div>
<div class="offcanvas-header">
  <h4 class="offcanvas-title">{{ dataId == null ? 'Add Country' : 'Update Country' }}</h4>
  <button type="button"  class="btn f_20 close_btn_tranlate" (click)="close('close')"><i class="fa-solid fa-xmark"></i></button>
</div>
<div class="offcanvas-body">
  <app-form-shimmer *ngIf="dataId != null && isLoading" [rowCount]="3" [type]="2"></app-form-shimmer>
  <form class="form_wrap" [formGroup]="country"   *ngIf="!isLoading"   novalidate (ngSubmit)="savecountry()">
    <div class="row">
      <div class="col-md-6">
        <div class="form_group">
          <label class="form-label">Country<span
            class="text-danger">*</span></label>
          <input type="text" class="form-control b_r_5" placeholder="Enter Your Country Name" formControlName="country_name"
          appNoEmptySpace [appMaxLength]="36" />
        </div>
        <div
          *ngIf="country.controls['country_name'].hasError('required') && (country.controls['country_name'].dirty || country.controls['country_name'].touched); ">
          <p class="text-danger"> Country Name is required</p>
          </div>
      </div>
      <div class="col-md-6">
        <div class="form_group">
          <label class="form-label">Phone Code<span
            class="text-danger">*</span></label>
          <input type="number" class="form-control b_r_5" placeholder="Enter Your Phone Code"  formControlName="phone_code"
          appNoEmptySpace [appMaxLength]="16" />
        </div>
        <div
          *ngIf="country.controls['phone_code'].hasError('required') && (country.controls['phone_code'].dirty || country.controls['phone_code'].touched); ">
          <p class="text-danger"> Phone Code is required</p>
          </div>
      </div>
      <div class="col-md-6">
        <div class="form_group">
          <label class="form-label">Currency Symbol<span
            class="text-danger">*</span></label>
          <input type="email" class="form-control b_r_5" placeholder="Enter Your Currency Symbol"  formControlName="currency_symbol"
          appNoEmptySpace [appMaxLength]="6" />
        </div>
        <div
        *ngIf="country.controls['currency_symbol'].hasError('required') && (country.controls['currency_symbol'].dirty || country.controls['currency_symbol'].touched); ">
        <p class="text-danger"> Currency Symbol is required</p>
          </div>
      </div>

      <div class="col-md-6">
        <div class="form_group">
          <label class="form-label">Capital<span
            class="text-danger">*</span></label>
          <input type="text" class="form-control b_r_5" placeholder="Enter Your Capital" formControlName="capital" appNoEmptySpace
          [appMaxLength]="20"/>
        </div>
        <div
        *ngIf="country.controls['capital'].hasError('required') && (country.controls['capital'].dirty || country.controls['capital'].touched); ">
        <p class="text-danger"> Capital is required</p>
          </div>
      </div>
      <div class="col-md-6">
        <div class="form_group">
          <label class="form-label">Currency Name<span
            class="text-danger">*</span></label>
          <input type="text" class="form-control b_r_5" formControlName="currency_name"
          appNoEmptySpace [appMaxLength]="39" />
        </div>
        <div
        *ngIf="country.controls['currency_name'].hasError('required') && (country.controls['currency_name'].dirty || country.controls['currency_name'].touched); ">
        <p class="text-danger"> Currency Name is required</p>
          </div>
      </div>
      <div class="col-md-6">
        <div class="form_group">
          <label class="form-label">Tld<span
            class="text-danger">*</span></label>
            <input class="form-control b_r_5" type="text" formControlName="tld"  appNoEmptySpace
            [appMaxLength]="3"/>
        </div>
      </div>

      <div class="col-md-12">
        <div class="text-end mt-3">
          <!-- <button class="btn btn_outline_primary me-2"  (click)="activeOffcanvas.close('close')">Cancel</button> -->
          <button type="button" class="btn btn_outline_primary me-2"  (click)="close('close')">Cancel</button>
          <button type="submit"  label="Submit" class="btn btn_primary">Save</button>
        </div>
      </div>
    </div>
  </form>
</div>
