import { Component, inject, Input } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { UserService } from 'src/app/services/user.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent {

  loginForm: FormGroup;
  submitted = false;

  constructor(
    private formBuilder: FormBuilder,
    private userService: UserService,
    private router: Router
  ) {

    const isAuthenticated = !!localStorage.getItem('token');
    if (isAuthenticated) {
      this.router.navigate(['/users']);
    }

    this.loginForm = this.formBuilder.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });


  }


  get f() {
    return this.loginForm.controls;
  }
  register(){
    this.router.navigate(['/register']);
  }

  login() {
    this.submitted = true;

    if (this.loginForm.invalid) {
      return;
    }

    let data = {
      "email":this.loginForm.value.email,
      "password":this.loginForm.value.password,
    }
    this.userService.login(data).then((result: any) => {
      if (result.status == 'SUCCESS') {
        localStorage.setItem('name', result.name);
        localStorage.setItem('id', result.id);
        localStorage.setItem('token', result.token);
        localStorage.setItem('role', result.role);
        // this.router.navigate(['/users']);
        window.location.href = "/users";
      }else{
        alert(result.message);
      }
    });

  }
}
<div class="back">


  <div class="div-center">


    <div class="content">


      <h3>Login</h3>
      <hr />
            <form [formGroup]="loginForm" (ngSubmit)="login()">
              <div class="form-group">
                <label>Email:</label>
                <input formControlName="email" type="email" class="form-control" />
                <div *ngIf="submitted && f['email'].errors">
                  <small *ngIf="f['email'].errors['required']">Email is required.</small>
                  <small *ngIf="f['email'].errors['email']">Please enter a valid email.</small>
                </div>
              </div>

              <div class="form-group">
                <label>Password:</label>
                <input formControlName="password" type="password" class="form-control" />
                <div *ngIf="submitted && f['password'].errors">
                  <small *ngIf="f['password'].errors['required']">Password is required.</small>
                  <small *ngIf="f['password'].errors['minlength']">Password must be at least 6 characters long.</small>
                </div>
              </div>

              <button type="submit" class="btn btn-primary">Login</button>
              <hr />
              <button type="button" class="btn btn-link">Signup</button>
              <button type="button" class="btn btn-link">Reset Password</button>

              <button type="submit">Login</button>
              <button type="button" (click)="register()">Register</button>
            </form>

      </div>
  </div>
</div>
